import React, { useContext, useState, useEffect } from 'react'
import AuthContext from '../Context/AuthContext'
import axios from 'axios'
import { Link, useHistory } from 'react-router-dom'

import { FlexboxGrid, Panel, Placeholder, Tag, TagGroup } from 'rsuite'
import { Empty } from 'antd'

export default function ArticoliSalvati() {

    const { Paragraph } = Placeholder

    const history = useHistory()

    const { user, urlServer } = useContext(AuthContext)

    const [posts, setPosts] = useState([])

    useEffect(() => {
        if (user !== undefined) {          
                user.preferiti.map(async id => {
                    await axios.post(`${urlServer}/post`, { id }).then(res => {
                        console.log("dati post preferiti", res.data)

                        if (res.data.length > 0) {
                            const e = res.data[0]

                            let temp = posts

                            temp.push(
                                < Link to={`/post/${e._id}`}>
                                    <Panel className="articolo" header={e.title} shaded>
                                        <label className="scrittaTag">Tag</label>
                                        <TagGroup className="tag">
                                            {e.tags.map(tag => {
                                                return (
                                                    <Tag>{tag}</Tag>
                                                )
                                            })}
                                        </TagGroup>
                                        <p className="description">
                                            {e.description}
                                        </p>
                                    </Panel>
                                </Link >
                            )

                            setPosts(temp)

                            history.push('/profile')
                        }
                    })
                })
        }
    }, [user])

    if (user === undefined) {
        return (
            <div>
                <Panel className="articolo" header={<Paragraph active />} shaded>
                    <Paragraph active />
                    <Paragraph active />
                    <Paragraph active />
                </Panel>
                <Panel className="articolo" header={<Paragraph active />} shaded>
                    <Paragraph active />
                    <Paragraph active />
                    <Paragraph active />
                </Panel>
                <Panel className="articolo" header={<Paragraph active />} shaded>
                    <Paragraph active />
                    <Paragraph active />
                    <Paragraph active />
                </Panel>
            </div>
        )
    }

    return (
        <div>
            {
                user.preferiti.length ? posts.map(e => e) : <FlexboxGrid align="middle"><Empty /></FlexboxGrid>
            }
        </div>
    )
}
