import React, { useContext, useState, useEffect } from 'react'
import Header from './Header'
import imgArticolo from '../img/articolo.png'
import imgProblema from '../img/problem-solving.png'
import AuthContext from '../Context/AuthContext'
import ArticoliSalvati from './ArticoliSalvati'
import ProblemiSalvati from './ProblemiSalvati'

import { Container, Sidenav, Sidebar, Content, Nav, Icon, FlexboxGrid, Panel } from 'rsuite'
import './Profile.css'

export default function Profile() {

    const { Paragraph } = Panel

    const { user } = useContext(AuthContext)

    const [option, setOption] = useState(true)

    const getUserName = () => {
        if(user) {
            return `${user.name} ${user.surname}`
        } else {
            return <Paragraph active />
        }
    }

    const getOption = () => {
        if(option === 1) {
            return <ArticoliSalvati />
        } else {
            return <ProblemiSalvati />
        }
    }

    return (
        <div>
            <Header />

            <Container>
                <Sidebar>
                    <Sidenav>
                        <Sidenav.Body>
                            <FlexboxGrid justify="center">
                                <div id="nomeUser">{getUserName()} <button><Icon icon="cog" /></button></div>
                            </FlexboxGrid>
                            <Nav>
                                <Nav.Item onClick={() => setOption(1)} icon={<img className="imgIcon" src={imgArticolo} alt="immagine di un articolo" />}>
                                    Articoli salvati
                                </Nav.Item>
                                <Nav.Item onClick={() => setOption(2)} icon={<img className="imgIcon" src={imgProblema} alt="incona pulsante problemi salvati" />}>
                                    Problemi salvati
                                </Nav.Item>
                            </Nav>
                        </Sidenav.Body>
                    </Sidenav>
                </Sidebar>
                <Content>
                    {getOption}
                </Content>
            </Container>
        </div>
    )
}
