import React, { useEffect, useContext, useState } from 'react'
import Header from './Header'
import { useParams, useHistory } from 'react-router'
import axios from 'axios'
import AuthContext from '../Context/AuthContext'
import Footer from './Footer'

import { Placeholder, FlexboxGrid, Icon } from 'rsuite'
import './Post.css'

export default function Post() {
    const { Paragraph } = Placeholder

    const history = useHistory()

    const { id } = useParams()

    const { urlServer, user } = useContext(AuthContext)

    const [post, setPost] = useState(false)

    useEffect(() => {
        axios.post(`${urlServer}/post/exact`, { id }).then(res => {
            console.log(res.data)
            setPost(res.data[0])
        })


    }, [])

    if (!post) {
        return (
            <div>
                <Header />
                <div className="Post">
                    <FlexboxGrid justify="center">
                        <p className="title">
                            <Paragraph active />
                        </p>
                    </FlexboxGrid>
                    <div className="text">
                        <Paragraph active />
                        <Paragraph active />
                        <Paragraph active />
                        <Paragraph active />
                        <Paragraph active />
                        <Paragraph active />
                        <Paragraph active />
                    </div>
                </div>
            </div>
        )
    }



    const save = (e) => {
        console.log(e.currentTarget.id)

        user.preferiti.push(e.currentTarget.id)

        setSavePost(true)

        axios.post(`${urlServer}/post/add-preferito`, { id: user._id, preferiti: user.preferiti }).then(res => {
            console.log(res)
        })
    }

    const remove = (e) => {
        let temp = []

        for (let i = 0; i < user.preferiti.length; i++) {
            if (user.preferiti[i] !== e.currentTarget.id) {
                temp.push(user.preferiti[i])
            }
        }

        user.preferiti = temp

        history.push(`/post/${id}`)

        axios.post(`${urlServer}/post/remove-preferito`, { id: user._id, preferiti: temp }).then(res => {
            console.log(res.data)

            console.log("user preferiti", user.preferiti, user.preferiti.includes(post._id))
        })
    }

    const savePost = () => {
        if (user) {
            let controllo = false;

            for (let i = 0; i < user.preferiti; i++) {
                if (user.preferiti[i] === post._id) {
                    controllo = true
                }
            }

            if (!controllo) {
                return <button id={post._id} onClick={remove} className="btnSave"><Icon icon="bookmark" size="2x" /></button>
            } else {
                return <button onClick={save} id={post._id} className="btnSave"><Icon icon="bookmark-o" size="2x" /></button>
            }
        }
    }

    return (
        <div>
            <div id="pagePost">
                <Header />
                <div className="Post">
                    <FlexboxGrid justify="end">
                        {savePost}
                    </FlexboxGrid>
                    <FlexboxGrid justify="center">
                        <p className="title">{post.title}</p>
                    </FlexboxGrid>
                    <div className="text">
                        {post.text}
                    </div>
                </div>
            </div>
            <Footer />
        </div>
    )
}
