import React, { useContext } from 'react'
import AuthContext from '../Context/AuthContext'
import axios from 'axios'
import { Link } from 'react-router-dom'

import { Panel, Placeholder, Tag, TagGroup } from 'rsuite'

export default function ArticoliSalvati() {

    const { Paragraph } = Placeholder

    const { user, urlServer } = useContext(AuthContext)

    if (user === undefined) {
        return (
            <div>
                <Panel className="articolo" header={<Paragraph active />} shaded>
                    <Paragraph active />
                    <Paragraph active />
                    <Paragraph active />
                </Panel>
                <Panel className="articolo" header={<Paragraph active />} shaded>
                    <Paragraph active />
                    <Paragraph active />
                    <Paragraph active />
                </Panel>
                <Panel className="articolo" header={<Paragraph active />} shaded>
                    <Paragraph active />
                    <Paragraph active />
                    <Paragraph active />
                </Panel>
            </div>
        )
    }

    return (
        <div>
            {user.preferiti.map( async id => {
                return await axios.post(`${urlServer}/post`, { id }).then(res => {
                    console.log("dati post preferiti", res.data)

                    const e = res.data[0]

                    return <Link to={`/post/${e._id}`}>
                        <Panel className="articolo" header={e.title} shaded>
                            <label className="scrittaTag">Tag</label>
                            <TagGroup className="tag">
                                {e.tags.map(tag => {
                                    return (
                                        <Tag>{tag}</Tag>
                                    )
                                })}
                            </TagGroup>
                            <p className="description">
                                {e.description}
                            </p>
                        </Panel>
                    </Link>
                })
            })}
        </div>
    )
}
