import React, { useContext, useState, useEffect } from 'react'
import AuthContext from '../Context/AuthContext'
import axios from 'axios'
import { Link, useHistory } from 'react-router-dom'

import { FlexboxGrid, Panel, Placeholder, Tag, TagGroup } from 'rsuite'
import { Empty } from 'antd'

export default function ArticoliSalvati() {

    const { Paragraph } = Placeholder

    const history = useHistory()

    const { user, urlServer } = useContext(AuthContext)

    const [posts, setPosts] = useState([])

    useEffect(() => {

    }, [])

    return (
        <div>
            {
                user.preferiti.length ? user.preferiti.map(id => {
                    const res = axios.post(`${urlServer}/post`, { id })

                    console.log("dati post preferiti", res.data)

                    const e = res.data[0]

                    return < Link to={`/post/${e._id}`}>
                        <Panel className="articolo" header={e.title} shaded>
                            <label className="scrittaTag">Tag</label>
                            <TagGroup className="tag">
                                {e.tags.map(tag => {
                                    return (
                                        <Tag>{tag}</Tag>
                                    )
                                })}
                            </TagGroup>
                            <p className="description">
                                {e.description}
                            </p>
                        </Panel>
                    </Link >
                }) : <Empty className="imgEmpty" />
            }
        </div>
    )
}
